mapper.select:
SELECT
    url_path AS urlPath,
    mobile_url_path AS mobileUrlPath
FROM sales_policy
WHERE
    contract_period = #{contractPeriod}
    AND activation_type = #{activationType}
    AND product_name = #{productName}
    AND plan_name = #{planName}
    AND sales_policy_name LIKE CONCAT('%', #{installmentType}, '%')
LIMIT 1
